import React from 'react'
import { useState,useEffect} from 'react';


const Github = () => {
    const[repos,setRepos] = useState([]);
    const [nrepo,setNrepo] = useState("0");
    const[repo2,setRepo2]=useState([]);
    const[repo3,setRepo3]=useState([]);
    const addMoreItem = (i,data) => {
        setRepos(prevItems => [...prevItems, {
          id: prevItems.length,
          value: data[i]
        }]);
      }
    useEffect(()=>{

        fetch(`https://api.github.com/users/s166harth/repos`)
        .then(res => res.json())
        .then(data=>(
          console.log(data),
          addMoreItem(0)




        ))

      },[])

  return (
    <>
    <p>{repos[0].name}</p>
    <p>{repos[0].description}</p>
    <a href="{repos[0].svn_url}">source</a>



    </>

  )
}

export default Github
