import logo from './logo.svg';
import './App.css';

function App() {



function requestUserRepos(username){


    const xhr = new XMLHttpRequest();


    const url = `https://api.github.com/users/${username}/repos`;



    xhr.open('GET', url, true);


    xhr.onload = function () {


        const repos = JSON.parse(this.response);


        for (let i in repos) {


            console.log(repos[i].name);

        }

    }


    xhr.send();

    for (let i in repos)
    {
      const commitsurl= ` https://api.github.com/repos/${username}/${repos[i].name}/commits`

      const xhrfc = new XMLHttpRequest();
      xhrfc.open(`GET`,commitsurl,true);
      xhrfc.onload = function () {


        const commits = JSON.parse(this.response);


        for (let i in commits) {


            console.log(commits[i]);

        }

    }
    xhrfc.send();

  }


}



